package jyl.util;

public class FloatCompare {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		FloatCompare fc = new FloatCompare();
		boolean equal = fc.compare(2.5f,2.5f);
		System.out.println("isequal : "+equal);
	}

	private boolean compare(float d, float e) {
		// TODO Auto-generated method stub
		System.out.println(d-e);
		return (d-e)!=0.0f;
	}

}
